(function(){tinymce.PluginManager.requireLangPack('filemanager');tinymce.create('tinymce.plugins.Filemanager',{init:function(ed,url){ed.addCommand('mceFilemanager_pic',function(){ed.windowManager.open({file:url+'../../../../../popups.php?action=tiny_uploader&var1=pic&var2=post&var3=newsfeld&returnvalue=tinymce',width:620,height:480,inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addCommand('mceFilemanager_file',function(){ed.windowManager.open({file:url+'../../../../../popups.php?action=tiny_uploader&var1=file&var2=post&var3=newsfeld&returnvalue=tinymce',width:620,height:480,inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('filemanager_pic',{title:'filemanager_pic.desc',cmd:'mceFilemanager_pic',image:url+'/img/_pics.gif'});ed.addButton('filemanager_file',{title:'filemanager_file.desc',cmd:'mceFilemanager_file',image:url+'/img/_files.gif'});ed.onNodeChange.add(function(ed,cm,n){})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Filemanager and Picmanager',author:'Michael Lorer',authorurl:'http://www.01-scripts.de',infourl:'',version:"1.0"}}});tinymce.PluginManager.add('filemanager',tinymce.plugins.Filemanager)})();